svg.script:
var a = document.querySelector('a'), rect = document.querySelector('rect'), effect = 'link';
a.addEventListener('dragstart',
function (event)
	{event.dataTransfer.effectAllowed = effect}
,false);
rect.addEventListener('dragenter',
function (event)
	{event.preventDefault();
	event.dataTransfer.effectAllowed = effect;}
,false);
rect.addEventListener('dragover',
function (event)
	{event.preventDefault()}
,false);
rect.addEventListener('drop',
function (event)
	{if(event.dataTransfer.dropEffect == effect && event.dataTransfer.effectAllowed == effect)
		{rect.setAttribute('fill','#228B22');}
	}
,false);
